function Review () {
   const [reviews, setMovies] = useState([])

   useEffect(() => {
    fetch ("http://localhost:9292/reviews")
    .then(response => response.json())
    .then ((movies) => setMovies(movies))
   }, [])

   let myComments = movies.map((movie) =>
         {movie.title}
   )

    return (
      <div className="reviews">
        {myComments}
      </div>
    )
  }

export default Review;


