import React, { Component, useEffect, useState } from "react";
import Comment from "./Comment";

function Review() {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch("http://localhost:9292/reviews")
      .then((response) => response.json())
      .then((reviews) => setReviews(reviews));
  }, []);

  let myReviews = reviews.map((review) => (
    // component to pass props
    // commentitem

    <Comment key={review.id} name={review.name} comment={review.comments} />
  ));
  console.log(reviews);
  return (
    <div className="comment-container" style={{ font: "" }}>

      <h1 className="comment" style={{ fontWeight: "900" }}>
        Please add your feedback here
      </h1>
      <form>
        <label for="fname">First name:</label>
        <input type="text" id="fname" name="fname" /> <br></br>
        <label for="lname">Comment:</label>
        <input type="text" id="lname " name="lname" /> <br></br>
        <input type="submit" value="Submit" />
      </form>

      {myReviews}
    </div>
  );
}

export default Review;
