function Review () {
   const [comments, setComments] = useState("")

   useEffect(() => {
    fetch ("http://localhost:9292/reviews")
    .then(response => response.json())
    .then (data => setComments(data.comment))
   }, [])

    return (
      <div className="reviews">
        {comments.map}
      </div>
    )
  }

export default Review;


