import React, { Component, useEffect,useState } from 'react'


function Review () {

   const [movies, setMovies] = useState([])

   useEffect(() => {
    fetch ("http://localhost:9292/movies")
    .then((response) => response.json())
    .then ((movies) => setMovies(movies))

   }, [])

   let myMovies = movies.map((movie) =>
         <Review title={movie.title}/>
   )

    return (
      <div>
        {myMovies}
        <h1>{movies.title}}</h1>
        <p>"The Red Ribbon Army, an evil organization that was once destroyed by Goku in the past, has been reformed by a group of people who have created new and mightier Androids, Gamma 1 and Gamma 2, and seek vengeance against Goku and his family."</p>

      </div>
    )
  }

export default Review;
